mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_TEL, MEMBER_NICKNAME, MEMBER_IMG_NAME, MEMBER_IMG_PATH, MEMBER_KAKAO_PROFILE_URL
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_IMG_NAME = #{memberImgName}, MEMBER_IMG_PATH = #{memberImgPath}
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_NICKNAME = #{memberNickname}
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_TEL = #{memberTel}, MEMBER_UPDATE_TIME = CURRENT_TIMESTAMP
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_STATUS = 'DISABLED'
WHERE ID = #{id}
